import styles from "./App.module.scss";
import { useState } from "react";
import Login from "./components/Forms/Login";
import Header from "./components/Header/Header";
import Navbar from "./components/NavBar/Navbar";


function App() {
    const [seeForm, setSeeForm] = useState(1);
    const [logged, setLogged] = useState(false);
    const [user, setUser] = useState(null);

    function seeRegisterForm() {
        setSeeForm(2);
    }

    function seeLoginForm() {
        setSeeForm(3);
    }

    function seeHomepage() {
        setSeeForm(1);
    }


    function toggleRegister() {
        setSeeForm(3);
        setLogged(true);
    }

    function toggleRegister2() {
        setSeeForm(1);
    }

    function toggleUnRegister() {
        setSeeForm(1);
        setLogged(false);
        setUser(null);
    }

    function getIdUser(userLogged) {
        setUser(userLogged);
    }

    function showProfile() {
        setSeeForm(4);
    }

    return (
        <>
            <Header />
            <Navbar
               seeLoginForm={seeLoginForm}
                seeRegisterForm={seeRegisterForm}
                toggleUnRegister={toggleUnRegister}
                showProfile={showProfile}
                logged={logged}
                user={user}
                seeHomepage={seeHomepage} />
            <Login 
            toggleRegister={toggleRegister}
            getIdUser={getIdUser} />
        </>
    );
}

export default App;
