import { useForm } from 'react-hook-form';
import styles from './Register.module.scss';
import * as yup from "yup"
import { yupResolver } from '@hookform/resolvers/yup';
import { useState } from 'react';


function Register({ seeLoginForm }) {
    const [feedback, setFeedback] = useState("");
    const [feedbackGood, setFeedbackGood] = useState("");
    const [isSubmitted, setIsSubmitted] = useState(false);

const yupSchema = yup.object({
    username : yup.string()
    .required("Le champ doit contenir au minimum 2 caractères ")
})


const defaultValues = {
    username: "",
    password: "",
    confirmPassword: "",
    email: "",
}

const { register,
        handleSubmit,
        reset, 
        formState : {errors}
    } = useForm({
        defaultValues, 
        mode :"onChange",
        resolver : yupResolver(yupSchema)
    })



    const submit = async (values) => {
        console.log(values);
        const response = await fetch("http://localhost:8009/api/users/addUser",{
            method : "POST",
            body : JSON.stringify(values),
            headers :{
                "Content-Type" : "application/json"
            }
        });
        if (response.ok) {
            const newUser = await response.json();
            console.log("newUser", newUser);
            if(newUser.message){
                setFeedback("Email déjà existant")
                setIsSubmitted(true)
            } else { setFeedbackGood(newUser.messageGood)
            reset(defaultValues);
        setTimeout(()=> {
            seeLoginForm();
        }, 3000);
    }
        }
setIsSubmitted(false)
    }

    return (
        <div className='"flex-fill d-flex flex-column justify-content-center align-items-center m30'>
            <form onSubmit={handleSubmit(submit)}>
                <div className="d-flex flex-column mb10">
                    <label htmlFor="username" className="mb10"> Username</label>
                    <input type="text" id="username" {...register("username")} />
                    {errors?.username && (<p className={`${styles.feedback}`} >{errors.username.message}</p>)}
                </div>

                <div className="d-flex flex-column mb10">
                    <label htmlFor="email" className="mb10"> Email</label>
                    <input type="text" id="email" {...register("email")} />
                    {errors?.email && (<p className={`${styles.feedback}`} >{errors.email.message}</p>)}
                </div>


                <div className="d-flex flex-column mb10">
                    <label htmlFor="password" className="mb10"> Password</label>
                    <input type="password" id="password" {...register("password")} />
                    {errors?.password && (<p className={`${styles.feedback}`} >{errors.password.message}</p>)}
                </div>

                <div className="d-flex flex-column mb10">
                    <label htmlFor="confirmPassword" className="mb10"> Confirm Password</label>
                    <input type="password" id="confirmPassword" {...register("confirmPassword")} />
                    {errors?.confirmPassword && (<p className={`${styles.feedback}`} >{errors.confirmPassword.message}</p>)}
                </div>

                {feedback && <p className={`${styles.feedback} mb20`}>{feedback} </p>}
                {feedbackGood && <p className={`${styles.feedbackGood} mb20`}>{feedbackGood} </p>}

                <button className="btn btn-primary" disabled={isSubmitted}>  Submit </button>
            </form>
        </div>
    );
}

export default Register;
