function Carrousel() {

    function moveToSelected(element) {

        if (element == "next") {
            const selected = $(".selected").next();
        } else if (element == "prev") {
            const selected = $(".selected").prev();
        } else {
            const selected = element;
        }

        const next = $(selected).next();
        const prev = $(selected).prev();
        const prevSecond = $(prev).prev();
        const nextSecond = $(next).next();

        $(selected).removeClass().addClass("selected");

        $(prev).removeClass().addClass("prev");
        $(next).removeClass().addClass("next");

        $(nextSecond).removeClass().addClass("nextRightSecond");
        $(prevSecond).removeClass().addClass("prevLeftSecond");

        $(nextSecond).nextAll().removeClass().addClass('hideRight');
        $(prevSecond).prevAll().removeClass().addClass('hideLeft');

    }


    $(document).keydown(function (e) {
        switch (e.which) {
            case 37: // left
                moveToSelected('prev');
                break;

            case 39: // right
                moveToSelected('next');
                break;

            default: return;
        }
        e.preventDefault();
    });

    $('#carousel div').click(function () {
        moveToSelected($(this));
    });

    $('#prev').click(function () {
        moveToSelected('prev');
    });

    $('#next').click(function () {
        moveToSelected('next');
    });


    return (
        <>
            <div id="carousel">
                <div className="hideLeft">
                    <img src="../../assets/images/accueil/eBook1.jpeg" alt="" />
                </div>

                <div className="prevLeftSecond">
                    <img src="../../assets/images/accueil/eBook2.jpeg" alt="" />
                </div>

                <div className="prev">
                    <img src="../../assets/images/accueil/eBook3.jpeg" alt="" />
                </div>

                <div className="selected">
                    <img src="../../assets/images/accueil/eBook1.jpeg" alt="" />
                </div>

                <div className="next">
                    <img src="../../assets/images/accueil/eBook2.jpeg" alt="" />
                </div>

                <div className="nextRightSecond">
                    <img src="../../assets/images/accueil/eBook3.jpeg" alt="" />
                </div>
                <div className="hideRight">
                    <img src="../../assets/images/accueil/eBook1.jpeg" alt="" />
                </div>

                <div class="buttons">
                    <button className="btn" id="prev">Prev</button>
                    <button className="btn" id="next">Next</button>
                </div>
            </div>
        </>
    );
}

export default Carrousel;
