import styles from "./App.module.scss";
import { useState } from "react";
import Login from "./components/Forms/Login";
import Header from "./components/Header/Header";
import Navbar from "./components/NavBar/Navbar";
import Homepage from "./components/pages/Homepage";
import Profile from "./components/pages/Profile";
import Register from "./components/Forms/Register"

function App() {
    const [seeForm, setSeeForm] = useState(1);
    const [logged, setLogged] = useState(false);
    const [user, setUser] = useState(null);

    const seeRegisterForm = () => {
        setSeeForm(2);
    };

    const seeLoginForm = () => {
        setSeeForm(3);
    };

    const seeHomepage = () => {
        setSeeForm(1);
    };


    function logout() {
        setSeeForm(3);
        setLogged(true);
    }


    function toggleUnRegister() {
        setSeeForm(1);
        setLogged(false);
        setUser(null);
    }

    function getIdUser(userLogged) {
        setUser(userLogged);
    }

    function seeProfile() {
        setSeeForm(4);
    }

    return (
        <div className={`d-flex flex-column  ${styles.appContainer}`}>
            <Header />
            <Navbar
                seeLoginForm={seeLoginForm}
                seeRegisterForm={seeRegisterForm}
                toggleUnRegister={toggleUnRegister}
                seeProfile={seeProfile}
                logged={logged}
                user={user}
                seeHomepage={seeHomepage} />
            {seeForm === 2 ? (
                <Register seeLoginForm={seeLoginForm} />
            ) : seeForm === 1 ? (
                <Login
                    logout={logout}
                    getIdUser={getIdUser} />
            ) : seeForm === 3 ? (<Homepage />) : (
                <Profile />
            )
            }
footer
        </div>
    );
}

export default App;
