import styles from "./App.module.scss";
import { useState } from "react";
import Login from "./components/Forms/Login";
import Header from "./components/Header/Header";
import Navbar from "./components/NavBar/Navbar";
import Homepage from "./components/pages/Homepage";
import Profile from "./components/pages/Profile";
import Register from "./components/Forms/Register";
import Footer from "./components/Footer/Footer";

function App() {

    const [seeComponent, setSeeComponent] = useState(1);
    const [user, setUser] = useState(null);

    console.log(user);

    const seeRegisterForm = () => {
        setSeeComponent(2);
    };

    const seeLoginForm = () => {
        setSeeComponent(3);
    };

    const seeHomepage = () => {
        setSeeComponent(1);
    };
    const seeProfile = () => {
        setSeeComponent(4);
    };
    const logout = () => {
        setSeeComponent(3);
        setUser(null);
    };

    const getUser = (userLogged) => {
        setUser(userLogged);
    };

    return (
        <div className={`d-flex flex-column  ${styles.appContainer}`}>
            <Header  seeLoginForm={seeLoginForm}
                seeRegisterForm={seeRegisterForm}
                seeHomepage={seeHomepage}
                user={user}
                seeProfile={seeProfile}
                logout={logout}/>

            {seeComponent === 1 ? (<Homepage />) : seeComponent === 2 ? (
                <Register seeLoginForm={seeLoginForm} />
            ) : seeComponent === 3 ? (
                <Login
                    seeHomepage={seeHomepage}
                    getUser={getUser} />
            ) : (<Profile />)
            }
            <Footer />
        </div>
    );
}

export default App;
