import React, { useState } from 'react'
import { useFieldArray, useForm } from "react-hook-form"
import * as yup from "yup"
import { yupResolver } from "@hookform/resolvers/yup"
import styles from "./Login.module.scss"

function Login({ seeHomepage, getUser }) {
    const [feedback, setFeedback] = useState("")
    const [feedbackGood, setFeedbackGood] = useState("")

    const yupSchema = yup.object({
        email: yup.string()
            .required("le champ est obligatoire")
            .email("vous devez saisir un email valide"),
        password: yup.string()
            .required("le champ est obligatoire")
    })


    const defaultValues = {
        password: "",
        email: "",
    }

    const { register,
        handleSubmit,
        reset,
        formState: { errors, isSubmitted }
    } = useForm({
        defaultValues,
        mode: "onChange",
        resolver: yupResolver(yupSchema)
    })


    const submit = async (values) => {
        setFeedback("");
        console.log(values);
        const response = await fetch("http://localhost:8009/api/users/login", {
            method: "POST",
            body: JSON.stringify(values),
            headers: {
                "Content-Type": "application/json"
            }
        });
        if (response.ok) {
            const newUser = await response.json();
            console.log("newUser", newUser);
            if (newUser.message) {
                setFeedback(newUser.message)
            }
            else {
                setFeedbackGood("Connexion réussie, vous allez être redirigé")
                reset(defaultValues);
                console.log("User recupéré", newUser);
                let user = {};
                user.username = newUser[0].username;
                user.email = newUser[0].email;
                user.preference = newUser[0].preference;
                user.free = newUser[0].free === 1 ? true : false;
                user.skills = [];

                console.log("user modifié", user);
                reset(defaultValues)
                setTimeout(() => {
                    getUser(user);
                    seeHomepage();
                }, 3000);
            }
        }
    }

        return (
            <div className='"flex-fill d-flex flex-column justify-content-center align-items-center m30'>
                <form onSubmit={handleSubmit(submit)}>
                    <div className="d-flex flex-column mb10">
                        <label htmlFor="email" className="mb10"> Email</label>
                        <input type="text" id="email" {...register("email")} />
                        {errors?.email && (<p className={`${styles.feedback}`} >{errors.email.message}</p>)}
                    </div>
                    <div className="d-flex flex-column mb10">
                        <label htmlFor="password" className="mb10"> Password</label>
                        <input type="password" id="password" {...register("password")} />
                        {errors?.password && (<p className={`${styles.feedback}`} >{errors.password.message}</p>)}
                    </div>

                    {feedback && <p className={`${styles.feedback} mb20`}>{feedback} </p>}
                    {feedbackGood && <p className={`${styles.feedbackGood} mb20`}>{feedbackGood} </p>}

                    <button className="btn btn-primary" disabled={isSubmitted}>  Submit </button>
                </form>
            </div>
        )
    }


export default Login
